import React from 'react';
import BoutonClique from './components/Exercice1/BoutonClique';
import BoutonBascule from './components/Exercice1/BoutonBascule';
import AppSuivi from './components/Exercice1/AppSuivi'; 
import Compteur from './components/Exercice1/Compteur';

import DisplayTab from './components/Exercise2/DisplayTab';       // Correct path for DisplayTab
import AuthForm from './components/Exercise3/AuthForm';          // Correct path for AuthForm
import DivCreator from './components/Exercise4/DivCreator';     // Correct path for DivCreator

import './App.css';

const App = () => {
  return (
    <div className="app-container">
      <section>
        <h2>Exemple 1 - Bouton Cliqué</h2>
        <BoutonClique />

        <h2>Exemple 2 - Bouton Bascule</h2>
        <BoutonBascule />

        <h2>Exemple 3 - Suivi des clics</h2>
        <AppSuivi />

        <h2>Exemple 4 - Compteur</h2>
        <Compteur />
      </section>

      <section>
        <h2>Exemple 5 - Affichage du Tableau</h2>
        <DisplayTab tab={["hello", "world", "from", "react"]} />

        <h2>Exemple 6 - Affichage de Deux Tableaux</h2>
        <DisplayTab tab={["apple", "banana", "cherry"]} />
      </section>

      <section>
        <h2>Exemple 7 - Formulaire d'Authentification</h2>
        <AuthForm />
      </section>

      <section>
        <h2>Exemple 8 - Création de Divs</h2>
        <DivCreator />
      </section>
    </div>
  );
}

export default App;
