import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

// Import components for each exercise
import Exercice1 from './components/Exercice1/Exercice1'; // New component

import DisplayTab from './components/Exercice2/DisplayTab';      
import AuthForm from './components/Exercice3/AuthForm';         
import DivCreator from './components/Exercice4/DivCreator';     

import './App.css';

const App = () => {
  return (
    <Router>
      <div className="app-container">
        <Routes>
          {/* Exercice 1 */}
          <Route path="/exercice1" element={<Exercice1 />} />

          {/* Exercice 2 */}
          <Route path="/exercice2/affichage-tableau" element={<DisplayTab tab={["hello", "world", "from", "react"]} />} />
          <Route path="/exercice2/deux-tableaux" element={<DisplayTab tab={["apple", "banana", "cherry"]} />} />

          {/* Exercice 3 */}
          <Route path="/exercice3/auth-form" element={<AuthForm />} />

          {/* Exercice 4 */}
          <Route path="/exercice4/div-creator" element={<DivCreator />} />

          {/* Default route */}
          <Route path="/" element={
            <div>
              <h1>Welcome to the Exercises</h1>
              <p>Choose an exercise:</p>
              <ul>
                <li><a href="/exercice1">Exercice 1</a></li> {/* Now links to Exercice1 component */}
                <li><a href="/exercice2/affichage-tableau">Exercice 2 - Affichage Tableau</a></li>
                <li><a href="/exercice2/deux-tableaux">Exercice 2 - Affichage Deux Tableaux</a></li>
                <li><a href="/exercice3/auth-form">Exercice 3 - Formulaire d'Authentification</a></li>
                <li><a href="/exercice4/div-creator">Exercice 4 - Cr√©ation de Divs</a></li>
              </ul>
            </div>
          } />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
