import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

// Import the components for each exercise
import BoutonClique from './components/Exercice1/BoutonClique';
import BoutonBascule from './components/Exercice1/BoutonBascule';
import AppSuivi from './components/Exercice1/AppSuivi';
import Compteur from './components/Exercice1/Compteur';
import DisplayTab from './components/Exercice2/DisplayTab';
import AuthForm from './components/Exercice3/AuthForm';
import DivCreator from './components/Exercice4/DivCreator';

// Import the CSS file
import './App.css';

const App = () => {
  return (
    <Router>
      <div className="app-container">
        <h1>React Exercises</h1>

        {/* Navigation links */}
        <nav>
          <ul>
            <li><a href="/ex1">Exercice 1</a></li>
            <li><a href="/ex2">Exercice 2</a></li>
            <li><a href="/ex3">Exercice 3</a></li>
            <li><a href="/ex4">Exercice 4</a></li>
          </ul>
        </nav>

        <Switch>
          {/* Define routes for each exercise */}
          <Route path="/ex1" exact>
            <h2>Exemple 1 - Bouton Cliqué</h2>
            <BoutonClique />

            <h2>Exemple 2 - Bouton Bascule</h2>
            <BoutonBascule />

            <h2>Exemple 3 - Suivi des clics</h2>
            <AppSuivi />

            <h2>Exemple 4 - Compteur</h2>
            <Compteur />
          </Route>

          <Route path="/ex2" exact>
            <h2>Exemple 5 - Affichage du Tableau</h2>
            <DisplayTab tab={["hello", "world", "from", "react"]} />
          </Route>

          <Route path="/ex3" exact>
            <h2>Exemple 6 - Formulaire d'Authentification</h2>
            <AuthForm />
          </Route>

          <Route path="/ex4" exact>
            <h2>Exemple 7 - Création de Divs</h2>
            <DivCreator />
          </Route>

          {/* Default route */}
          <Route path="/" exact>
            <h2>Welcome to React Exercises</h2>
            <p>Select an exercise to view its implementation.</p>
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
