import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

// Import components for each exercise
import BoutonClique from './components/Exercice1/BoutonClique';
import BoutonBascule from './components/Exercice1/BoutonBascule';
import AppSuivi from './components/Exercice1/AppSuivi'; 
import Compteur from './components/Exercice1/Compteur';

import DisplayTab from './components/Exercice2/DisplayTab';      
import AuthForm from './components/Exercice3/AuthForm';         
import DivCreator from './components/Exercice4/DivCreator';     

import './App.css';

const App = () => {
  return (
    <Router>
      <div className="app-container">
        <Switch>
          {/* Exercice 1 */}
          <Route path="/exercice1/bouton-clique" component={BoutonClique} />
          <Route path="/exercice1/bouton-bascule" component={BoutonBascule} />
          <Route path="/exercice1/suivi" component={AppSuivi} />
          <Route path="/exercice1/compteur" component={Compteur} />

          {/* Exercice 2 */}
          <Route path="/exercice2/affichage-tableau" render={() => <DisplayTab tab={["hello", "world", "from", "react"]} />} />

          <Route path="/exercice2/deux-tableaux" render={() => <DisplayTab tab={["apple", "banana", "cherry"]} />} />

          {/* Exercice 3 */}
          <Route path="/exercice3/auth-form" component={AuthForm} />

          {/* Exercice 4 */}
          <Route path="/exercice4/div-creator" component={DivCreator} />

          {/* Default route */}
          <Route path="/" exact>
            <h1>Welcome to the Exercises</h1>
            <p>Choose an exercise:</p>
            <ul>
              <li><a href="/exercice1/bouton-clique">Exercice 1 - Bouton Cliqué</a></li>
              <li><a href="/exercice1/bouton-bascule">Exercice 1 - Bouton Bascule</a></li>
              <li><a href="/exercice1/suivi">Exercice 1 - Suivi des clics</a></li>
              <li><a href="/exercice1/compteur">Exercice 1 - Compteur</a></li>
              <li><a href="/exercice2/affichage-tableau">Exercice 2 - Affichage Tableau</a></li>
              <li><a href="/exercice2/deux-tableaux">Exercice 2 - Affichage Deux Tableaux</a></li>
              <li><a href="/exercice3/auth-form">Exercice 3 - Formulaire d'Authentification</a></li>
              <li><a href="/exercice4/div-creator">Exercice 4 - Création de Divs</a></li>
            </ul>
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
