import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

// Import components for each exercise
import BoutonClique from './components/Exercice1/BoutonClique';
import BoutonBascule from './components/Exercice1/BoutonBascule';
import AppSuivi from './components/Exercice1/AppSuivi'; 
import Compteur from './components/Exercice1/Compteur';

import DisplayTab from './components/Exercice2/DisplayTab';      
import AuthForm from './components/Exercice3/AuthForm';         
import DivCreator from './components/Exercice4/DivCreator';     

import './App.css';

const App = () => {
  return (
    <Router>
      <div className="app-container">
        <Routes>
          {/* Default Route */}
          <Route path="/" element={
            <div>
              <h1>Welcome to the Exercises</h1>
              <p>Choose an exercise:</p>
              <ul>
                <li><a href="/exercice1">Exercice 1</a></li>
                <li><a href="/exercice2">Exercice 2</a></li>
                <li><a href="/exercice3">Exercice 3</a></li>
                <li><a href="/exercice4">Exercice 4</a></li>
              </ul>
            </div>
          } />

          {/* Exercice 1 Routes */}
          <Route path="/exercice1" element={<h2>Exercice 1</h2>}>
            <Route path="bouton-clique" element={<BoutonClique />} />
            <Route path="bouton-bascule" element={<BoutonBascule />} />
            <Route path="suivi" element={<AppSuivi />} />
            <Route path="compteur" element={<Compteur />} />
          </Route>

          {/* Exercice 2 Routes */}
          <Route path="/exercice2" element={<h2>Exercice 2</h2>}>
            <Route path="affichage-tableau" element={<DisplayTab tab={["hello", "world", "from", "react"]} />} />
            <Route path="deux-tableaux" element={<DisplayTab tab={["apple", "banana", "cherry"]} />} />
          </Route>

          {/* Exercice 3 Route */}
          <Route path="/exercice3" element={<h2>Exercice 3</h2>}>
            <Route path="auth-form" element={<AuthForm />} />
          </Route>

          {/* Exercice 4 Route */}
          <Route path="/exercice4" element={<h2>Exercice 4</h2>}>
            <Route path="div-creator" element={<DivCreator />} />
          </Route>
        </Routes>
      </div>
    </Router>
  );
}

export default App;
